import styled from "styled-components";

export const MaisonMariaThereza = styled.div`
  font-family: 'Roboto Condensed', sans-serif;
  font-size: 14px;
  font-weight: 400;

  .inner-wrapper {
    
    .content {
      border: 1px dashed black;
      align-items: center;
      display: flex;
      justify-content: center;
      margin: 30px 0;
      padding: 0 15px;
      width: 100vw;

      @media (max-width: 768px) {
        flex-direction: column;
      }
    }

    p {
      font-size: 14px;
      padding-bottom: 30px;
    }

    img {
      border: thick double rgba(22, 74, 115, .9);
      border-radius: 3px;
      height: 90%;
      width: 90%;
    }

    .navbarLink {
      color: #111;
      padding: 3px 9px;
    }

    .boxImg {
      align-items: center;
      border: 1px solid red;
      margin: 0px 25px;
      height: 450px;
      text-align: center;
      width: 45vw;

      &__tipologia {
        flex-wrap: wrap;
        width: 100%;
      }

      @media (max-width: 768px) {
        text-align: center;
      }
    }


  .boxText {
    border: 0px solid blue;
    align-items: center;
    height: 21rem;
    justify-content: center;
    line-height: 2;
    margin: 0px 2.5px;
    padding: 0px 15px;
    width: 45vw;

    @media (max-width: 768px) {
      padding: 0 10px;
      margin: 45px 5px;
      width: 100vw;
    }
  }
  
  .titulo {
    align-items: center;
    background: linear-gradient(rgba(22, 74, 115, .6), rgba(22, 74, 115, .9));
    border-radius: 1px 100px / 120px;
    color: yellow;
    font-family: 'Dancing Script';
    font-size: 1.8rem;
    padding: 6px 21px;
    text-align: center;
    width: 45vw;

    @media (max-width: 768px) {
      font-size: 1.2rem;
    }
  }

  .subTitulo {
    font-size: 1.2rem;
    padding: 15px 30px;
    text-align: center;
    
    @media (max-width: 768px) {
      font-size: 1rem;
    }
  }

  .descricao {
    align-items: center;
    border-radius: 6px;
    display: flex;
    font-size: 1.5rem;
    justify-content: center;
    line-height: 1.2;
    padding: 5px 30px;
    text-align: justify;

    @media (max-width: 768px) {
      font-size: .8rem;
    }

    li {
      list-style: square;
    }
  }


  .box_pavimento {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      margin: 5px 0;
      text-align: justify;
      width: 100%;

      @media (max-width: 768px) {
        margin: 15px 0;        
      }
  }

  .subTitulo_pavimento {
    padding: 5px;
    text-align: center;
  }

  .descricao_pavimento {
    align-items: center;
    display: flex;
    justify-content: center;
  }
}
`;
